import React, { useState, useRef, useEffect } from "react";
import Banner from "./Banner";

const TerminalWindow = () => {
  const [history, setHistory] = useState([]);
  const [language, setLanguage] = useState("pt");
  const [showCursor, setShowCursor] = useState(true); // controle do cursor
  const terminalEndRef = useRef(null);

  const content = {
    pt: {
      about: "Olá! Sou Charles Menezes, especialista em Linux com mais de 10 anos de experiência e desenvolvedor.",
      projects: "Confira meu GitHub: https://github.com/charlesmenez",
      skills: "Linux, React, JavaScript, Python, Shell scripting",
      contact: "Email: charles@example.com",
      translate: "en_US",
      clear: "Limpar",
    },
    en: {
      about: "Hi! I'm Charles Menezes, Linux expert with 10+ years of experience and developer.",
      projects: "Check out my GitHub: https://github.com/charlesmenez",
      skills: "Linux, React, JavaScript, Python, Shell scripting",
      contact: "Email: charles@example.com",
      translate: "pt_BR",
      clear: "Clear",
    },
  };

  // Scroll automático
  useEffect(() => {
    terminalEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [history]);

  // Animação de digitação corrigida
  const typeText = (text, cmd) => {
    setHistory((prev) => [...prev, { command: cmd, output: "" }]);
    let i = 0;

    const interval = setInterval(() => {
      setHistory((prev) => {
        const newHistory = [...prev];
        const lastItem = newHistory[newHistory.length - 1];

        if (i < text.length) {
          lastItem.output += text[i];
          i++;
        } else {
          clearInterval(interval);
        }

        return newHistory;
      });
    }, 30);
  };

  const handleCommand = (cmd) => {
    if (cmd === "clear") {
      setHistory([]);
    } else {
      const text = content[language][cmd];
      typeText(text, cmd);
    }

    // sempre que um comando é executado, mostra o cursor e esconde depois de 4s
    setShowCursor(true);
    setTimeout(() => setShowCursor(false), 4000);
  };

  const toggleLanguage = () => {
    setLanguage(language === "pt" ? "en" : "pt");
  };

  return (
    <div className="window">
      <div className="window-header">
        <div className="buttons">
          <span className="close"></span>
          <span className="minimize"></span>
          <span className="maximize"></span>
        </div>
        <span className="title">Portfólio Terminal - Charles Menezes</span>
        <div className="header-buttons">
          <button className="translate-btn" onClick={toggleLanguage}>
            {language === "pt" ? content.pt.translate : content.en.translate}
          </button>
          <button className="clear-btn" onClick={() => handleCommand("clear")}>
            {language === "pt" ? content.pt.clear : content.en.clear}
          </button>
        </div>
      </div>

      <div className="terminal-content">
        <Banner />
        <div className="history">
          {history.map((h, i) => (
            <div key={i} className="fade-in">
              <div className="command">
                &gt; {h.command}{" "}
                {showCursor && <span className="cursor">█</span>}
              </div>
              <div className="output">{h.output}</div>
            </div>
          ))}
          <div ref={terminalEndRef} />
        </div>

        <div className="buttons-panel">
          <button onClick={() => handleCommand("about")}>
            {language === "pt" ? "Sobre" : "About"}
          </button>
          <button onClick={() => handleCommand("projects")}>
            {language === "pt" ? "Projetos" : "Projects"}
          </button>
          <button onClick={() => handleCommand("skills")}>
            {language === "pt" ? "Habilidades" : "Skills"}
          </button>
          <button onClick={() => handleCommand("contact")}>
            {language === "pt" ? "Contato" : "Contact"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default TerminalWindow;
