import React, { useState } from "react";
import Banner from "./Banner";

const TerminalWindow = () => {
  const [history, setHistory] = useState([]);
  const [language, setLanguage] = useState("pt"); // default Portuguese

  const content = {
    pt: {
      about: "Olá! Sou Charles Menezes, especialista em Linux com mais de 10 anos de experiência e desenvolvedor.",
      projects: "Confira meu GitHub: https://github.com/charlesmenez",
      skills: "Linux, React, JavaScript, Python, Shell scripting",
      contact: "Email: charles@example.com",
      help: "Comandos disponíveis: sobre, projetos, habilidades, contato, ajuda",
      translate: "EN-US",
    },
    en: {
      about: "Hi! I'm Charles Menezes, Linux expert with 10+ years of experience and developer.",
      projects: "Check out my GitHub: https://github.com/charlesmenez",
      skills: "Linux, React, JavaScript, Python, Shell scripting",
      contact: "Email: charles@example.com",
      help: "Available commands: about, projects, skills, contact, help",
      translate: "PT-BR",
    },
  };

  const handleCommand = (cmd) => {
    setHistory([...history, { command: cmd, output: content[language][cmd] }]);
  };

  const toggleLanguage = () => {
    setLanguage(language === "pt" ? "en" : "pt");
  };

  return (
    <div className="window">
      <div className="window-header">
        <div className="buttons">
          <span className="close"></span>
          <span className="minimize"></span>
          <span className="maximize"></span>
        </div>
        <span className="title">Portfólio Terminal - Charles Menezes</span>
        <button className="translate-btn" onClick={toggleLanguage}>
          {language === "pt" ? content.pt.translate : content.en.translate}
        </button>
      </div>
      <div className="terminal-content">
        <Banner />
        <div className="history">
          {history.map((h, i) => (
            <div key={i}>
              <div className="command">&gt; {h.command}</div>
              <div className="output">{h.output}</div>
            </div>
          ))}
        </div>
        <div className="buttons-panel">
          <button onClick={() => handleCommand("about")}>{language === "pt" ? "Sobre" : "About"}</button>
          <button onClick={() => handleCommand("projects")}>{language === "pt" ? "Projetos" : "Projects"}</button>
          <button onClick={() => handleCommand("skills")}>{language === "pt" ? "Habilidades" : "Skills"}</button>
          <button onClick={() => handleCommand("contact")}>{language === "pt" ? "Contato" : "Contact"}</button>
          <button onClick={() => handleCommand("help")}>{language === "pt" ? "Ajuda" : "Help"}</button>
        </div>
      </div>
    </div>
  );
};

export default TerminalWindow;
