import React, { useState } from "react";
import Banner from "./Banner";

const TerminalWindow = () => {
  const [input, setInput] = useState("");
  const [history, setHistory] = useState([]);

  const commands = {
    about: "Hi! I'm Charles Menezes, a Linux expert with 10+ years of experience and a developer.",
    projects: "GitHub: https://github.com/charlesmenez",
    skills: "Linux, React, JavaScript, Python, Shell scripting",
    contact: "Email: charles@example.com",
    help: "Available commands: about, projects, skills, contact, help",
  };

  const handleCommand = (e) => {
    e.preventDefault();
    const cmd = input.toLowerCase();
    const output = commands[cmd] || `Command not found: ${cmd}`;
    setHistory([...history, { command: cmd, output }]);
    setInput("");
  };

  return (
    <div className="window">
      <div className="window-header">
        <div className="buttons">
          <span className="close"></span>
          <span className="minimize"></span>
          <span className="maximize"></span>
        </div>
        <span className="title">Charles's Terminal Portfolio</span>
      </div>
      <div className="terminal-content">
        <Banner />
        <div className="history">
          {history.map((h, i) => (
            <div key={i}>
              <div className="command">&gt; {h.command}</div>
              <div className="output">{h.output}</div>
            </div>
          ))}
        </div>
        <form onSubmit={handleCommand}>
          <span>&gt; </span>
          <input
            value={input}
            onChange={(e) => setInput(e.target.value)}
            autoFocus
            className="terminal-input"
          />
        </form>
      </div>
    </div>
  );
};

export default TerminalWindow;
